<script lang="ts">
	import type { PortraitOptions } from '$lib/ts/interfaces/PortraitOptions';

	export let portraitValues: PortraitOptions = {};

	function lightenDarkenColor(hexColor: string, amount: number) {
		let color = hexColor;

		if (hexColor.charAt(0) === '#') {
			hexColor = hexColor.slice(1);
		}

		// parse the color as an integer
		var num = parseInt(hexColor, 16);

		// get the rgb values and clamp them
		var r = (num >> 16) + amount;
		var b = ((num >> 8) & 0x00ff) + amount;
		var g = (num & 0x0000ff) + amount;
		r = Math.min(255, Math.max(0, r));
		b = Math.min(255, Math.max(0, b));
		g = Math.min(255, Math.max(0, g));

		// convert the rgb value back to hex format
		var newColor = (g | (b << 8) | (r << 16)).toString(16);

		// add the "#" back if needed
		return (color[0] === '#' ? '#' : '') + newColor;
	}

	$: skin = portraitValues['Skin Color']?.variant;
	$: skinShadow = lightenDarkenColor(skin, -40);
</script>

